using UnityEngine;public class CardController : MonoBehaviour{    // Start is called before the first frame update    public CardView view;    public CardModel Card;    public CardMovement movement;     // カードの移動    private void Awake() {        view = GetComponent<CardView>();        movement = GetComponent<CardMovement>();    }    public void Init(string cardName, bool isPlayerCard) {        Card =  new CardModel(cardName, isPlayerCard);        view.Show(Card);            }    public void Vanish() {        Destroy(this.gameObject);    }}