using System.Collections;using System.Collections.Generic;using UnityEngine;public class CardController : MonoBehaviour{    // Start is called before the first frame update    public CardView view;    public CardModel card;    public CardMovement movement;     // カードの移動    private void Awake() {        view = GetComponent<CardView>();        movement = GetComponent<CardMovement>();    }    public void Init(string cardName, bool isPlayerCard) {        card =  new CardModel(cardName, isPlayerCard);        view.Show(card);            }    public void Vanish() {        Destroy(this.gameObject);    }}